
public class Unary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10; // 10 value will be stored into a variable
		a++; // increase the value by 1
		// a contain 11
		System.out.println("value of a is>>" + a);
		a--; // decrease the value by 1
		// a contain 10
		System.out.println("value of a is>>" + a);

	}

}
